    private void blistaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_blistaActionPerformed
        // TODO add your handling code here:
        try
        {
            num=Integer.parseInt(textingreso.getText());
            if(lista1.Existe(num)==true)
            {
                mtexto.setText("Si se encuentra en la Lista");
            }
            else
            {
                mtexto.setText("Numero no est√° en la Lista");
            }
        }
        catch(Exception ex)
        {
            textingreso.setText("DATOS INVALIDOS");
        }
        
    }//GEN-LAST:event_blistaActionPerformed

